    private void B_confermaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_B_confermaActionPerformed
        String username = TF_username.getText();
        String Password = TF_password.getText();
        LocalDate referenceDate = LocalDate.now();
        String userInput = TF_data.getText();
        DateTimeFormatter formatter = DateTimeFormatter.ofPattern("dd/MM/yyyy");
        LocalDate dateOfBirth = LocalDate.parse(userInput, formatter);
        
        Period age = Period.between(dateOfBirth, referenceDate);

        // Verificare se la persona è maggiorenne (>= 18 anni)
        if (age.getYears() >= 18) {
            if(Informativa.isSelected()){
            File inputFile = new File("input.txt");
            try {
            // Utilizza uno Scanner per leggere il contenuto del file
            Scanner scanner = new Scanner(inputFile);
            StringBuilder fileContent = new StringBuilder();
            while (scanner.hasNextLine()) {
                fileContent.append(scanner.nextLine()).append(System.lineSeparator());
            }
            scanner.close();
            
            // Aggiungi la nuova riga formata dalle due variabili separate da una virgola
            String newLine = username + "," + Password + System.lineSeparator();
            fileContent.append(newLine);
            
            // Scrivi il nuovo contenuto nel file
            BufferedWriter writer = new BufferedWriter(new FileWriter("input.txt"));
            writer.write(fileContent.toString());
            writer.close();
        } catch (IOException e) {
            // Gestisci le eccezioni (stampa il messaggio di errore)
            e.printStackTrace();
        }
        this.dispose();
            }
            else{
                Errore.setText("Per registrarti devi prima accettare l'informativa sulla privacy!");
                Errore.setVisible(true);
            }
        } else {
            Errore.setText("Si è verificato un errore, per registrarti devi essere maggiorenne!");
            Errore.setVisible(true);
        }
       
        
    }//GEN-LAST:event_B_confermaActionPerformed

